INSERT INTO `about` VALUES (1,'公司简介','<p style=\"text-align: center;\"><span style=\"font-size: 20px; font-family: 微软雅黑, &#39;microsoft yahei&#39;;\">&nbsp; &nbsp;&nbsp;<img src=\"/jlk/lib/ueditor/php/upload/image/20170113/1484268040703139.jpg\" title=\"1484268040703139.jpg\" alt=\"560385898066380334.jpg\"/></span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;microsoft yahei&#39;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size: 16px; font-family: 微软雅黑, &#39;microsoft yahei&#39;; color: rgb(63, 63, 63);\">&nbsp;&nbsp;泉州市洁立康环保工程有限公司是一家专业研发生产销售环保设备、智能自来水管清洗机、多功能家电清洗设备、空气净化设备、清洁用品、清洁工具、水系统净化服务的创新环保企业。公司拥有先进的生产技术，更有一批具有创新精神的专业技术人才和服务团队，随着人民生活水平提高，水污染日渐增多，针对家庭生活用水污染问题，公司引进国外技术生产的自来水管道清洗机，采用智能管道清洗机和气泵提供气源及自来水管道中的水，形成脉冲螺旋气流，通过波段震荡、螺旋气泡和水振波产生强劲的水槌能快速清除剥离自来水管道中水垢、绿藻、铁锈，环保健康。</span></span></p>'),(2,'发展战略','<p>&nbsp; &nbsp;<span style=\"font-family: 宋体; font-size: 14px;\"><strong><span style=\"font-family: 宋体; font-size: 16px;\">核心价值观：</span><span style=\"font-family: 宋体; font-size: 16px;\"></span></strong>诚信、创新、人本、专注</span></p><p>&nbsp; &nbsp;<strong><strong style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px;\">诚信：</span></strong></strong><span style=\"font-family: 宋体; font-size: 14px;\">诚信是企业立身之本，一要对顾客诚信，二要对员工诚信，三药对股东诚信，四要对政府诚信，五要对社会诚信。</span> &nbsp; &nbsp;</p><p><span style=\"font-family: 宋体; font-size: 14px;\"></span></p><p>&nbsp; &nbsp;<strong><span style=\"font-family: 宋体;\"><strong style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px;\">创新：</span></strong></span></strong><span style=\"font-family: 宋体; font-size: 14px;\">没有创新就没有发展，创新是企业进步的灵魂，是事业前进的不竭动力。一要文化创新，二要管理创新，三要技术创新。</span></p><p>&nbsp; &nbsp;<strong><span style=\"font-family: 宋体; font-size: 16px;\"><strong style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px;\">人本：</span></strong></span></strong><span style=\"font-size: 14px;\">以人为本是企业成功的关键，把面向人，尊重人，关爱人放在管理首位，做顾客、员工、股东的知心人、贴心人；只有学习才能成功。</span></p><p>&nbsp;&nbsp;&nbsp;<strong><strong style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px;\">专注：</span></strong></strong><span style=\"font-size: 14px;\">专注是企业成功的瑰宝，一个企业只有专注品质，关注未来，才能成功。</span><br/></p><p>&nbsp; &nbsp;<strong><strong style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px;\">企业格言：</span></strong></strong><span style=\"font-size: 14px;\">洁立康生活卫视，为您的健康生活保驾护航，创新科技引领高端新生活。</span></p><p>&nbsp; &nbsp;<strong><strong style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px;\">企业使命：</span></strong></strong><span style=\"font-size: 14px;\">引领环保健康新时尚,缔造财富自由新企业。</span></p><p>&nbsp; &nbsp;<span style=\"font-size: 14px;\"><strong><strong><strong style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px;\">企业远景：</span></strong></strong></strong>缔造最具创新力和竞争力的环保企业</span></p><p>&nbsp; &nbsp;<strong><strong><strong style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px;\">企业精神：</span></strong></strong></strong><span style=\"font-size: 14px;\">专注品质，关注未来。</span></p><p><br/></p><p><br/></p>'),(3,'团队风采','<p>发给热</p>'),(4,'合作伙伴',''),(5,'公司荣誉','');
